const menuBtn=document.querySelector(".menu__btn"),menuList=document.querySelector(".header__menu");function toggleTwoClasses(e,s,n,t){e.classList.contains(s)?(e.classList.add(n),window.setTimeout((function(){e.classList.remove(s)}),t)):(e.classList.add(s),e.classList.remove(n))}menuBtn.addEventListener("click",(()=>{toggleTwoClasses(menuList,"menu--open","menu--close",500)})),document.onclick=function(e){"menu__btn-img"!==e.target.className&&"menu__item-link"!==e.target.className&&(menuList.classList.add("menu--close"),window.setTimeout((function(){menuList.classList.remove("menu--open")}),500))};